CREATE TABLE "almacenes_almacen" ("a_codigo" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "a_nombre" text NOT NULL, "a_direccion" text NOT NULL, "a_creado" date NOT NULL, "a_modificado" date NOT NULL)
CREATE TABLE "almacenes_inventario" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "i_cantidad" integer NOT NULL UNIQUE, "i_creado" date NOT NULL, "i_modificado" date NOT NULL, "a_codigo" integer NOT NULL REFERENCES "almacenes_almacen" ("a_codigo") DEFERRABLE INITIALLY DEFERRED, "r_codigo" integer NOT NULL REFERENCES "referencias_referencia" ("r_codigo") DEFERRABLE INITIALLY DEFERRED)
CREATE TABLE "auth_user" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NULL, "is_superuser" bool NOT NULL, "username" varchar(150) NOT NULL UNIQUE, "last_name" varchar(150) NOT NULL, "email" varchar(254) NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL, "date_joined" datetime NOT NULL, "first_name" varchar(150) NOT NULL)
CREATE TABLE "movimientos_movimiento" ("m_codigo" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "m_nombre" text NOT NULL, "m_operador" varchar(5) NOT NULL, "m_tipo" varchar(2) NOT NULL, "m_creado" date NOT NULL, "m_modificado" date NOT NULL)
CREATE TABLE "referencias_referencia" ("r_codigo" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "r_nombre" text NOT NULL, "r_marca" text NOT NULL, "r_observacion" text NOT NULL, "r_creado" date NOT NULL, "r_modificado" date NOT NULL) 
CREATE TABLE "usuarios_perfil" ("p_usuario" integer NOT NULL PRIMARY KEY REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "p_cargo" text NOT NULL, "p_creado" date NOT NULL, "p_modificado" date NOT NULL)